import {
  ISearchResultState,
  ISearchResultActions,
  SET_SEARCH_RESULT,
} from "./types";

const initialState: ISearchResultState = {
  repos: null,
};

export const repoSearchReducer = (
  state = initialState,
  action: ISearchResultActions
): ISearchResultState => {
  switch (action.type) {
    case SET_SEARCH_RESULT:
      return {
        repos: action.payload,
      };
    default:
      return state;
  }
};
