import React, { useState } from "react";
import classNames from "classnames";
import AutocompleteItem from "components/AutocompleteItem";

const Search = ({ onSearch }) => {
  const [inputFocused, setInputFocused] = useState(false);
  const onInputFocus = () => {
    setInputFocused(true);
  };
  const onInputBlur = () => {
    setInputFocused(false);
  };
  const onEnter = (event) => {
    if (event.key === "Enter") {
      console.log(event.target.value);
    }
  };
  return (
    <div className="search">
      <div
        className={classNames("search__input", {
          "search__input--focus": inputFocused,
        })}
      >
        <input
          type="text"
          placeholder="Search or jump to..."
          onFocus={onInputFocus}
          onBlur={onInputBlur}
          onKeyDown={onEnter}
        />

        <img
          src="https://github.githubassets.com/images/search-key-slash.svg"
          alt="Search icon"
        />
      </div>
      <div className="search__autocomplete">
        <div className="autocomplete">{/* <AutocompleteItem /> */}</div>
      </div>
    </div>
  );
};

export default Search;
