import { SET_SEARCH_RESULT, ISearchResultState } from "./types";
import { ISearchRepository } from "interfaces";
import { gitApi } from "utils/api";

const parseRepos = (repos) => {
  return repos.map((item) => {
    const {
      full_name,
      owner,
      html_url,
      description,
      stargazers_count,
      language,
      license,
      updated_at,
    } = item;

    return {
      full_name,
      owner: owner.login,
      html_url,
      description,
      stargazers_count,
      language,
      license: license.name,
      updated_at,
    };
  });
};

const Actions = {
  setSearchResult: (searchResult: ISearchResultState) => {
    return {
      type: SET_SEARCH_RESULT,
      payload: searchResult,
    };
  },
  fetchSearch: (searchQuery: ISearchRepository) => async (dispatch) => {
    const repos = parseRepos(await gitApi.searchRepository(searchQuery));
    console.log(repos);
  },
};

export default Actions;
