import classNames from "classnames";
import "styles/Pagination.global.scss";

const paginationValues = (count, current) => {
  const res = [];
  if (count > 9) {
    res.push("1");
    res.push("2");
    if (current > 5) {
      res.push("...");
      res.push((current - 2).toString());
      res.push((current - 1).toString());
      res.push(current.toString());
      res.push((current + 1).toString());
      res.push((current + 2).toString());
      if (count - current > 3) {
        res.push("...");
        res.push((count - 1).toString());
        res.push(count.toString());
      } else {
        res.push((count - 2).toString());
        res.push((count - 1).toString());
        res.push(count.toString());
      }
    } else {
      res.push("3");
      res.push("4");
      res.push("5");
      res.push("...");
      res.push((count - 2).toString());
      res.push((count - 1).toString());
    }
  } else {
    for (let i = 1; i <= current; i++) res.push(i.toString());
  }
  return res;
};

const Pagination: React.FC<{ count: number; current: number }> = ({
  count,
  current = 1,
}) => {
  console.log(count, current);
  const paginationArray = paginationValues(count, current);
  console.log(paginationArray);
  return (
    <div className="pagination">
      <div className="pagination__item pagination__item--disable">Previous</div>
      {paginationArray.map((item, index) => {
        return (
          <div
            key={index}
            className={classNames("pagination__item", {
              "pagination__item--enable": item === current.toString(),
              "pagination__item--disable": item === "...",
            })}
          >
            {item}
          </div>
        );
      })}
      {/* <div className="pagination__item pagination__item--enable">1</div>
      <div className="pagination__item">2</div>
      <div className="pagination__item">3</div>
      <div className="pagination__item pagination__item--disable">...</div>
      <div className="pagination__item">5</div>
      <div className="pagination__item">6</div>
      <div className="pagination__item">7</div>
      <div className="pagination__item">8</div> */}
      <div className="pagination__item">Next</div>
    </div>
  );
};

export default Pagination;
