import classNames from "classnames";
import "styles/Pagination.global.scss";

const paginationValues = (count, current) => {
  const res = [];
  if (count > 9) {
    res.push("1");
    res.push("2");
    if (current > 5) {
      res.push("...");
      res.push((current - 2).toString());
      res.push((current - 1).toString());
      res.push(current.toString());
      res.push((current + 1).toString());
      res.push((current + 2).toString());
      if (count - current > 3) {
        res.push("...");
        res.push((count - 1).toString());
        res.push(count.toString());
      } else {
        res.push((count - 2).toString());
        res.push((count - 1).toString());
        res.push(count.toString());
      }
    } else {
      res.push("3");
      res.push("4");
      res.push("5");
      res.push("...");
      res.push((count - 2).toString());
      res.push((count - 1).toString());
    }
  } else {
    for (let i = 1; i <= current; i++) res.push(i.toString());
  }
  return res;
};

const PaginationItem: React.FC<{
  disable: boolean;
  enable: boolean;
  text: string;
  onClick: Function;
}> = ({ disable, enable, text, onClick }) => {
  return (
    <div
      className={classNames("pagination__item", {
        "pagination__item--enable": enable,
        "pagination__item--disable": disable,
      })}
      onClick={() => onClick(text)}
    >
      {text}
    </div>
  );
};

const Pagination: React.FC<{
  count: number;
  current: number;
  onClick: Function;
}> = ({ count, current = 1, onClick }) => {
  const paginationArray = paginationValues(count, current);
  return (
    <div className="pagination">
      <PaginationItem
        disable={current === 1}
        enable={false}
        text="Previous"
        onClick={onClick}
      />
      {paginationArray.map((item, index) => {
        return (
          <PaginationItem
            disable={item === "..."}
            enable={item === current.toString()}
            text={item}
            onClick={onClick}
          />
        );
      })}
      <PaginationItem
        disable={current === count}
        enable={false}
        text="Next"
        onClick={onClick}
      />
    </div>
  );
};

export default Pagination;
