import { SET_SEARCH_RESULT } from "./types";
import { IRepositoryItem, ISearchRepository } from "interfaces";
import { gitApi } from "utils/api";

const Actions = {
  setSearchResult: (searchResult: IRepositoryItem[]) => {
    return {
      type: SET_SEARCH_RESULT,
      payload: searchResult,
    };
  },
  fetchSearch: (searchQuery: ISearchRepository) => async (dispatch) => {
    return await gitApi.searchRepository(searchQuery);
  },
};

export default Actions;
