import React, { useState } from "react";
import classNames from "classnames";
import AutocompleteItem from "components/AutocompleteItem";
import { searchCache } from "utils";

const Search = ({ onSearch }) => {
  const [inputFocused, setInputFocused] = useState(false);
  const [inputValue, setInputValue] = useState("");
  const onInputFocus = () => {
    setInputFocused(true);
  };
  const onInputBlur = () => {
    setInputFocused(false);
  };
  const onInputChange = (event) => {
    setInputValue(event.target.value);
  };
  const onEnter = (event) => {
    if (event.key === "Enter") {
      onSearch(event.target.value);
      searchCache.writeTo(event.target.value);
      event.target.blur();
    }
  };
  return (
    <div className="search">
      <div
        className={classNames("search__input", {
          "search__input--focus": inputFocused,
        })}
      >
        <input
          type="text"
          placeholder="Search or jump to..."
          onFocus={onInputFocus}
          onBlur={onInputBlur}
          onKeyDown={onEnter}
          onChange={onInputChange}
          value={inputValue}
        />

        <img
          src="https://github.githubassets.com/images/search-key-slash.svg"
          alt="Search icon"
        />
      </div>
      <div className="search__autocomplete">
        {inputFocused && (
          <div className="autocomplete">
            <AutocompleteItem
              searchCache={searchCache.searchIn(inputValue)}
              search={inputValue}
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default Search;
