import { SET_SEARCH_RESULT, ISearchResultState } from "./types";
import { ISearchRepository } from "interfaces";
import { gitApi } from "utils/api";

const Actions = {
  setSearchResult: (searchResult: ISearchResultState) => {
    return {
      type: SET_SEARCH_RESULT,
      payload: searchResult,
    };
  },
  fetchSearch: (searchQuery: ISearchRepository) => async (dispatch) => {
    let repos = await gitApi.searchRepository(searchQuery);
    repos = parseRepos(repos.items);
    console.log(repos);
    repos = addTopics(repos);
    console.log(repos);
  },
};

export default Actions;

const parseRepos = (repos) => {
  return repos.map((item) => {
    const {
      name,
      full_name,
      owner,
      html_url,
      description,
      stargazers_count,
      language,
      license,
      updated_at,
    } = item;

    return {
      name,
      full_name,
      owner: owner.login,
      html_url,
      description,
      stargazers_count,
      language,
      license: license.name,
      updated_at,
    };
  });
};

const addTopics = (repos) => {
  return Promise.all(
    repos.map(async (item) => {
      const topics = await gitApi.getRepositoryTopics(item.owner, item.name);

      return {
        topics: topics.names,
        ...item,
      };
    })
  );
};
